'use strict';

var core = require('@emotion/core');

var fields = require('@arch-ui/fields');

var dayPicker = require('@arch-ui/day-picker');
/** @jsx jsx */


const DateTimeField = ({
  autoFocus,
  field,
  onChange,
  value,
  errors
}) => {
  const htmlID = `ks-input-${field.path}`;
  return core.jsx(fields.FieldContainer, null, core.jsx(fields.FieldLabel, {
    htmlFor: htmlID,
    field: field,
    errors: errors
  }), core.jsx(fields.FieldDescription, {
    text: field.adminDoc
  }), core.jsx(fields.FieldInput, null, core.jsx(dayPicker.TextDayTimePicker, {
    id: htmlID,
    date: value,
    onChange: onChange,
    autoFocus: autoFocus
  })));
};

exports.default = DateTimeField;
