import { jsx } from '@emotion/core';
import { FieldContainer, FieldLabel, FieldDescription, FieldInput } from '@arch-ui/fields';
import { TextDayPicker } from '@arch-ui/day-picker';
import { Alert } from '@arch-ui/alert';
/** @jsx jsx */

const CalendarDayField = ({
  autoFocus,
  field,
  value,
  errors,
  onChange
}) => {
  const htmlID = `ks-daypicker-${field.path}`;
  return jsx(FieldContainer, null, jsx(FieldLabel, {
    htmlFor: htmlID,
    field: field,
    errors: errors
  }), jsx(FieldDescription, {
    text: field.adminDoc
  }), jsx(FieldInput, null, jsx(TextDayPicker, {
    id: htmlID,
    autoFocus: autoFocus,
    date: value,
    format: field.config.format,
    onChange: onChange
  })), errors.map(({
    message,
    data
  }) => jsx(Alert, {
    appearance: "danger",
    key: message
  }, message, data ? ` - ${JSON.stringify(data)}` : null)));
};

export default CalendarDayField;
