'use strict';

var core = require('@emotion/core');

var fields = require('@arch-ui/fields');

var dayPicker = require('@arch-ui/day-picker');

var alert = require('@arch-ui/alert');
/** @jsx jsx */


const CalendarDayField = ({
  autoFocus,
  field,
  value,
  errors,
  onChange
}) => {
  const htmlID = `ks-daypicker-${field.path}`;
  return core.jsx(fields.FieldContainer, null, core.jsx(fields.FieldLabel, {
    htmlFor: htmlID,
    field: field,
    errors: errors
  }), core.jsx(fields.FieldDescription, {
    text: field.adminDoc
  }), core.jsx(fields.FieldInput, null, core.jsx(dayPicker.TextDayPicker, {
    id: htmlID,
    autoFocus: autoFocus,
    date: value,
    format: field.config.format,
    onChange: onChange
  })), errors.map(({
    message,
    data
  }) => core.jsx(alert.Alert, {
    appearance: "danger",
    key: message
  }, message, data ? ` - ${JSON.stringify(data)}` : null)));
};

exports.default = CalendarDayField;
