'use strict';

var dateFns = require('date-fns');

const CalendarDayCell = ({
  data,
  field: {
    config
  }
}) => {
  if (!data) {
    return null;
  }

  if (!config.format) {
    return data;
  }

  return dateFns.format(dateFns.parseISO(data), config.format);
};

exports.default = CalendarDayCell;
